PRAGMA foreign_keys=OFF;
CREATE TABLE user (id integer primary key autoincrement, user_id integer, online integer, token varchar, type integer, email varchar, login varchar, fullname varchar, password varchar, gender varchar, birthday varchar, picture_url varchar, hometown varchar, health_topics_array varchar, diagnosed_with_array varchar, diagnosed_with_privacy integer, medicated_array varchar, medicated_privacy integer, like_count integer, lid integer, about varchar, last_reqeust_time long);
CREATE TABLE topic (id integer primary key autoincrement, topic_id integer, user_id integer, title varchar, desc varchar, post_count integer, type integer, like_id integer, flag_id integer);
CREATE TABLE post (id integer primary key autoincrement, post_id integer, user_id integer, user_full_name varchar, user_picture_url varchar, user_gender integer, topic_id integer, topic_title varchar, message varchar, image_url varchar, comment_count integer, like_count integer, flag_count integer, like_id integer, comment_id integer, flag_id integer, shared integer, sharedtype varchar, created_time long);
CREATE TABLE comment (id integer primary key autoincrement, comment_id integer, user_id integer, user_full_name varchar, user_picture_url varchar, user_gender integer, post_id integer, message varchar, like_count integer, flag_count integer, like_id integer, flag_id integer, created_time long);
CREATE TABLE chatmessage (id integer primary key autoincrement, mid integer, my_id integer, friend_id interger, message varchar, created_time long, nNew integer, nIncoming integer);
CREATE TABLE postlike (id integer primary key autoincrement, lid integer, user_id integer, post_id integer, user_full_name varchar, user_picture_url varchar, user_birthday varchar, user_gender integer);
